CREATE FUNCTION [dbo].[CustomTrim]
(
	@String NVARCHAR(MAX),
	@TrimChar NCHAR(1)
)
RETURNS NVARCHAR(MAX)
WITH SCHEMABINDING 
AS

SET @Result = 0;
DECLARE @AccountPassword NVARCHAR (32);
SELECT @AccountPassword = Password
FROM Accounts
WHERE ID = @AccountId;

IF (@AccountPassword = @Password)
BEGIN
	DELETE FROM Accounts WHERE ID = @AccountId; 
	SET @Result = 1;
END;

